import { useContext, useEffect, useState } from 'react';
import CartContext from '../../store/cart-context';
import classes from './styles/HeaderButton.module.css';
import HeaderButtonView from './HeaderButtonView';

function HeaderButton(props) {

    const [btnIsHighlighted, setBtnIsHighLighted] = useState(true);

    const cartCtx = useContext(CartContext);

    const numberOfCartItems = cartCtx.items.reduce((curNumber, item) => {
        return curNumber + item.amount
    }, 0);

    const btnClasses = `${classes.button} ${btnIsHighlighted ? classes.bump : ''}`;

    useEffect(() => {
        if (cartCtx.items.length === 0) {
            return;
        }
        setBtnIsHighLighted(true);
        const timer = setTimeout(() => {
            setBtnIsHighLighted(false);
        }, 300);

        return () => {
            clearTimeout(timer);
        }
    }, [cartCtx.items])

    return (
        <HeaderButtonView onClick={props.click} btnClasses={btnClasses} numberOfCartItems={numberOfCartItems} />
    )
}

export default HeaderButton
