import { useContext } from 'react';
import CartContext from '../../store/cart-context';
import CartView from './CartView';

function CartContainer(props) {
  const cartCtx = useContext(CartContext);
  const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;
  const hasItem = cartCtx.items.length > 0;

  const cartItemAddHandler = (item) => {
    cartCtx.addItem({ ...item, amount: 1 });
  }

  const cartItemRemoveHandler = (id) => {
    cartCtx.removeItem(id);
  }

  const orderHandler = () => {
    props.onHideCart();
    props.onShowOrder();
  }

  return (
    <CartView items={cartCtx.items} cartItemRemoveHandler={cartItemRemoveHandler} cartItemAddHandler={cartItemAddHandler}
      onHideCart={props.onHideCart} orderHandler={orderHandler} totalAmount={totalAmount} hasItem={hasItem}
    />
  )
}

export default CartContainer
